import React from 'react';
import {
  arrayOf,
} from 'prop-types';
// import pokemons from './data';

import Pokemon from './Pokemon';
import { pokemonType } from './types';
import Button from './Button';

class Pokedex extends React.Component {
  constructor() {
    super();
    this.state = {
      index: 0,
      type: 'all',
    };
  }

  nextPokemon = (numberOfPokemons) => {
    this.setState((state) => ({
      index: (state.index + 1) % numberOfPokemons,
    }));
  }

  filteredPokemons = () => {
    const { pokemons } = this.props;
    const { type } = this.state;

    return pokemons.filter((pokemon) => {
      if (type === 'all') return true;
      return pokemon.type === type;
    });
  }

  render() {
    const filteredPokemon = this.filteredPokemons;
    const { index } = this.state;
    const pokemon = filteredPokemon[index];
    const { pokemons } = this.props;
    console.log(pokemons);
    return (
      <div className="pokedex">
        <Pokemon key={ pokemon.id } pokemon={ pokemon } />
        {/* { pokemons.map((pokemon) => <Button text={ pokemon.type } key={ pokemon.id } />) } */}
        {/* <Button text="Proximo pokemon" /> */}
      </div>
    );
  }
}

Pokedex.propTypes = {
  pokemons: arrayOf(pokemonType).isRequired,
};

export default Pokedex;
