import React, { Component } from 'react';
import './App.css';
import UserProfile from './components/UserProfile';

class App extends Component {
  constructor() {
    super();
    this.state = {
      showProfile: true,
      apiReturn: '',
      loading: true,
    }
  }

  async componentDidMount() {
    const URL = 'https://api.randomuser.me/';
    try {
      this.setState( async () => {
        const promisse = await fetch(URL);
        const response = await promisse.json();
        return { apiReturn: response };
        console.log(this.state.apiReturn);
      }, () => {
        return { loading: false,}
      })
    } catch (error) {
      alert(error.message)
    }

  }

  render() {
    const { loading, apiReturn } = this.state;
    return (
      <div>
        { loading ? <div>loading...</div> : <UserProfile apiReturn={ apiReturn }/>}
      </div>
    )
  }
}

export default App;
