import React, { Component } from 'react';
import './App.css';
import UserProfile from './components/UserProfile';

class App extends Component {
  constructor() {
    super();
    this.state = {
      showProfile: true,
      apiReturn: '',
      loading: true,
    }
  }

  async componentDidMount() {
    const URL = 'https://api.randomuser.me/';
    const promisse = await fetch(URL);
    const response = await promisse.json();
    this.setState({
      apiReturn: response,
      loading: false,
    })
  }

  shouldComponentUpdate(_nextProps, nextState) {
    console.log(nextState);
    const AGE = 50;
    if (nextState.results[0].dob.age > AGE) {
      return false;
    }
    return true;
  }

render() {
  const { loading, apiReturn } = this.state;
  return (
    <div>
      {loading ? <div>loading...</div> : <UserProfile apiReturn={apiReturn} />}
    </div>
  )
}
}

export default App;
