import React, { Component } from 'react';
import './App.css';
import UserProfile from './components/UserProfile';

class App extends Component {
  constructor() {
    super();
    this.state = {
      showProfile: true,
      apiReturn: '',
      dataReturn: '', 
      loading: true,
    }
  }

  async componentDidMount() {
    const URL = 'https://api.randomuser.me/';
    const promisse = await fetch(URL);
    const response = await promisse.json();
    this.setState({
      apiReturn: response.results,
      loading: false,
    })
  }

  // componentDidUpdate() {
  //   this.getData();
  // }

  getData = () => {
    const { apiReturn } = this.state;
    const result = apiReturn[0];
    console.log(result);
    this.setState({
      dataReturn:{
        src: result.picture.medium,
        alt: result.name.last,
        name: `${result.name.first} ${result.name.last}`,
        email: `${result.email}`,
        age: `${result.dob.age}`
      }
    })
  }
  shouldComponentUpdate(_nextProps, nextState) {
    // console.log(nextState);
    const AGE = 50;
    if (nextState.apiReturn[0].dob.age > AGE) {
      return false;
    }
    return true;
  }

  render() {
    const { loading, dataReturn } = this.state;
    // console.log(apiReturn);
    return (
      <div>
        {loading ? <div>loading...</div> : <UserProfile dataReturn={dataReturn} />}
      </div>
    )
  }
}

export default App;
