import React, { Component } from 'react';
import './App.css';
import UserProfile from './components/UserProfile';

class App extends Component {
  constructor() {
    super();
    this.state = {
      showProfile: true,
      apiReturn: '',
      loading: true,
    }
  }

  async componentDidMount() {
    const URL = 'https://api.randomuser.me/';
    const promisse = await fetch(URL);
    const response = await promisse.json();
    this.setState({
      apiReturn: response.results,
      loading: false,
    })
  }

  // componentDidUpdate() {
  //   this.getData();
  // }

  getData = () => {
    const { apiReturn } = this.state;
    const result = apiReturn[0];
    console.log(result);
    return (
      <div>
        <img src={result.picture.medium} alt={result.name.last}></img>
        <p>`${result.name.first} ${result.name.last}`</p>
        <p>`${result.email}`</p>
        <p>`${result.dob.age}`</p>
      </div> 
    )
  }
  shouldComponentUpdate(_nextProps, nextState) {
    // console.log(nextState);
    const AGE = 50;
    if (nextState.apiReturn[0].dob.age > AGE) {
      return false;
    }
    return true;
  }

render() {
  const { loading, apiReturn } = this.state;
  // console.log(apiReturn);
  return (
    <div>
      {loading ? <div>loading...</div> : <UserProfile apiReturn={apiReturn} />}
    </div>
  )
}
}

export default App;
