import React from 'react';
import PropTypes from 'prop-types';

export default function Mails({ emails, setEmails }) {
  const readEmail = (id) => {
    // const index = emails.findIndex((email) => email.id === id);
    const updatedMessage = emails.map((email) => {
      if(email.id === id) {
        return { ...email, status: email.status === 0 ? 1 : 0}
      } else {
        return email;
      }
    })
    setEmails(updatedMessage)
  };
  return (
    <div>
      <button>Marcar todas como lidas</button>
      <button>Marcar todas como n√£o lidas</button>
      <div>
        <ul>
          {emails.map((email) => {
            return(
              <li
                key={email.id}
                onClick={() => readEmail(email.id)}
                className={email.status === 0 ? 'unread' : 'read'}
              >
                {email.title}
              </li>
            )
          } )}

        </ul>
      </div>
    </div>
  )
}

Mails.propTypes = {
  emails: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      title: PropTypes.string,
      status: PropTypes.number,
    }),
  ).isRequired,
};
