import React from 'react';
import PropTypes from 'prop-types';

export default function Mails({ emails }) {
  const readEmail = (id) => {
    const index = emails.find((email) => email.id === id);
    console.log(index.indexOf());
  };
  return (
    <div>
      <button>Marcar todas como lidas</button>
      <button>Marcar todas como n√£o lidas</button>
      <div>
        <ul>
          {emails.map((email) => {
            return(
              <li key={email.id} onClick={() => readEmail(email.id)}>{email.title}</li>
            )
          } )}

        </ul>
      </div>
    </div>
  )
}

Mails.propTypes = {
  emails: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      title: PropTypes.string,
      status: PropTypes.number,
    }),
  ).isRequired,
};
