import React from 'react';
import PropTypes from 'prop-types';

export default function Mails({ emails, setEmails }) {
  const readEmail = (id) => {
    const updatedMessage = emails.map((email) => {
      if(email.id === id) {
        return { ...email, status: email.status === 1 }
      } else {
        return {...email};
      }
    })
    setEmails(updatedMessage)
  };

  const unreadEmail = (id) => {
    const updatedMessage = emails.map((email) => {
      if(email.id === id) {
        return { ...email, status: email.status === 0 }
      } else {
        return {...email};
      }
    })
    setEmails(updatedMessage)
  };

  return (
    <div>
      <button>Marcar todas como lidas</button>
      <button>Marcar todas como não lidas</button>
      <div>
        <ul>
          {emails.map((email) => {
            return(
              <li
                key={email.id}
                className={email.status === 0 ? 'unread' : 'read'}
              >
                {email.title}
                <button key={`read${email.id}`} onClick={() => readEmail(email.id)}>Lido</button>
                <button key={`unread${email.id}`} onClick={() => unreadEmail(email.id)}>Não lido</button>
              </li>
            )
          } )}

        </ul>
      </div>
    </div>
  )
}

Mails.propTypes = {
  emails: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      title: PropTypes.string,
      status: PropTypes.number,
    }),
  ).isRequired,
};
