import { useEffect, useState } from 'react';
import './App.css';
import Mails from './components/Mails';

function App() {
  const [emails, setEmails] = useState([]);
  useEffect(() => {
    const emailsToSend = [
      {
        id: 1,
        title: "Primeiro email",
        status: 0,
      },
      {
        id: 2,
        title: "Primeiro email",
        status: 0,
      },
      {
        id: 3,
        title: "Primeiro email",
        status: 0,
      },
    ];
    setEmails(emailsToSend);
  }, []);
  return (
    <div className="App">
      <header><h1>TrybeMail</h1></header>
      <Mails emails={ emails }/>
    </div>
  );
}

export default App;
