// App.js
import React from 'react';
import useTimer from './hooks/useTimer';
import './App.css';

export default function Timer() {
  const { timer } = useTimer();
  return (
    <div className="App">
      <p>{`Tempo: ${timer}`}</p>
    </div>
  );
}
